<?php
class TK_Form_Radiobutton extends TK_Form_Element{	var $checked;
	/**	 * PHP 4 constructor	 *	 * @package Themekraft Framework	 * @since 0.1.0	 * 	 * @param array $args Array of [ $id Id, $name Name, $value Value, $extra Extra textfield code ]	 */	function tk_form_radiobutton( $args = array() ){		$this->__construct( $args );			}
	/**	 * PHP 5 constructor	 *	 * @package Themekraft Framework	 * @since 0.1.0	 * 	 * @param array $args Array of [ $id Id, $name Name, $value Value, $extra Extra textfield code ]	 */	function __construct( $args = array() ){		$defaults = array(			'id' => '',			'name' => '',			'value' => '',			'checked' => FALSE,			'css_classes' => '',			'extra' => '',			'multi_index' => '',			'before_element' => '',			'after_element' => ''		);				$parsed_args = wp_parse_args( $args, $defaults);		extract( $parsed_args , EXTR_SKIP );				parent::__construct( $parsed_args );				$this->checked = $checked;		$this->extra = $extra;				$this->before_element = $before_element;		$this->after_element = $after_element;			}		/**	 * Getting HTML of textfield	 *	 * @package Themekraft Framework	 * @since 0.1.0	 * 	 * @return string $html The html of the textfield	 */	function get_html(){		if( $this->id != '' ) $id = ' id="' . $this->id . '"';		if( $this->name != '' ) $name = ' name="' . $this->name . '"';		if( $this->value != '' ) $value = ' value="' . $this->value . '"';		if( $this->checked == TRUE ) $checked = ' checked';		if( $this->extra != '' ) $extra = $this->extra;				$html = $this->before_element;		$html.= '<input' . $id . $name . $value . $extra . $checked .  ' type="radio" />';		$html.= $this->after_element;				return $html;	}}function tk_radiobutton( $args, $return_object = false ){	$radiobutton = new TK_Form_Radiobutton( $args );		if( TRUE == $return_object ){		return $radiobutton;	}else{		return $radiobutton->get_html();	}}