<?phpclass TK_Form_Textarea extends TK_Form_Element{		var $rows;	var $cols;		/**	 * PHP 4 constructor	 *	 * @package Themekraft Framework	 * @since 0.1.0	 * 	 * @param array $args Array of [ $id Id, $name Name, $value Value, $extra Extra textarea code, $rows Number of rows in textarea, $cols Number of columns in textarea, $before_textarea Code before textarea, $after_textarea Code after textarea ]	 */	function tk_form_textarea( $args = array() ){		$this->__construct( $args );	}		/**	 * PHP 5 constructor	 *	 * @package Themekraft Framework	 * @since 0.1.0	 * 	 * @param array $args Array of [ $id Id, $name Name, $value Value, $extra Extra textarea code, $rows Number of rows in textarea, $cols Number of columns in textarea, $before_textarea Code before textarea, $after_textarea Code after textarea ]	 */	function __construct( $args = array() ){				$defaults = array(			'id' => $this->get_id(),			'name' => $this->get_id(),			'value' => '',			'rows' => '',			'cols' => '',			'css_classes' => '',			'extra' => '',			'multi_index' => FALSE,			'before_element' => '',			'after_element' => ''		);		$parsed_args = wp_parse_args( $args, $defaults );		extract( $parsed_args , EXTR_SKIP );		// Putting Args to parent		$args = array(			'id' => $id,			'name' => $name,			'value' => $value,			'css_classes' => $css_classes,			'extra' => $extra,			'multi_index' => $multi_index,			'before_element' => $before_element,			'after_element' => $after_element		);		parent::__construct( $args );		// New Values		$this->rows = $rows;		$this->cols = $cols;				if( $this->rows != '' ) $this->str_rows = ' rows="' . $this->rows . '"';		if( $this->cols != '' ) $this->str_cols = ' cols="' . $this->cols . '"';	}	/**	 * Getting HTML of textfield	 *	 * @package Themekraft Framework	 * @since 0.1.0	 * 	 * @return string $html The html of the textfield	 */	function get_html(){		$html = $this->before_element;		// Linedtextarea should move		$html.= '<div class="linedtextarea lined"><textarea' . $this->str_id . $this->str_name . $this->str_rows . $this->str_cols . $this->extra . ' />' . $this->value  . '</textarea></div>';		$html.= $this->after_element;				return $html;	}}function tk_textarea( $name, $args = array(), $return_object = FALSE ){	$textarea = new TK_Form_Textarea( $name, $args );		if( TRUE == $return_object ){		return $textarea;	}else{		return $textarea->get_html();	}	}