<?php
class TK_WP_Form_Radiobutton extends TK_Form_Radiobutton{	var $option_group;
	/**	 * PHP 4 constructor	 *	 * @package Themekraft Framework	 * @since 0.1.0	 * 	 * @param string $name Name of radiobutton	 * @param array $args Array of [ $id,  $extra Extra radiobutton code, $option_group Name of optiongroup where textfield have to be saved, $before_radiobutton Code before radiobutton, $after_radiobutton Code after radiobutton    ]	 */	function tk_wp_form_radiobutton( $name, $value, $args = array() ){		$this->__construct( $name, $value, $args );	}		/**	 * PHP 5 constructor	 *	 * @package Themekraft Framework	 * @since 0.1.0	 * 	 * @param string $name Name of radiobutton	 * @param array $args Array of [ $id,  $extra Extra radiobutton code, $option_group Name of optiongroup where textfield have to be saved, $before_radiobutton Code before radiobutton, $after_radiobutton Code after radiobutton    ]	 */	function __construct( $name, $value, $args = array() ){		global $post, $tk_form_instance_option_group;				$defaults = array(			'option_group' => $tk_form_instance_option_group,			'id' => '',			'default_value' => '',			'checked' => FALSE,			'css_classes' => '',			'extra' => '',			'multi_index' => '',			'before_element' => '',			'after_element' => ''		);				$args = wp_parse_args( $args, $defaults );		extract( $args , EXTR_SKIP );		// Getting value		$value = tk_get_value( $name, array( 'option_group' => $option_group, 'multi_index' => $multi_index, 'default_value' => $default_value ) );				// Putting Args to parent		$args = array(			'id' => $id,			'name' => $name,			'css_classes' => $css_classes,			'value' => $value,			'checked' => $checked,			'extra' => $extra,			'multi_index' => $multi_index,			'before_element' => $before_element,			'after_element' => $after_element		);		parent::__construct( $args );				// Rewriting Fieldname and Input Field String for WP Savings		$name = tk_get_field_name( $name, array( 'option_group' => $option_group, 'multi_index' => $multi_index ) );		$this->str_name = ' name="' . $name . '"';	}}
function tk_form_radiobutton( $name, $value,  $args = array(), $return_object = FALSE ){	$radiobutton = new TK_WP_Form_Radiobutton( $name, $value, $args );
	if( TRUE == $return_object ){		return $radiobutton;	}else{		return $radiobutton->get_html();	}}